
export const isString = (input: unknown) => typeof input === `string`

export const isNumber = (input: unknown) => typeof input === `number`

export const isNotNull = (input: unknown) => input !== null

export const isNull = (input: unknown) => input === null

export const isZero = (input: unknown) => input === 0

export const isOne = (input: unknown) => input === 1
